mapper.select:
select m.* from ms_order_ref m
  where m.userId = #{userId}
  and m.goodsId = #{goodsId}

select * from ms_order t
where 1=1
and t.userId = #{userId}
and t.goodsId = #{goodsId}

mapper.insert:
insert into ms_order_ref(
  userId,
  orderId,
  goodsId
)values(
  #{userId},
  #{orderId},
  #{goodsId}
)
